import React from "react";

export default function RecipeEditIngredient(props) {
  const {ingredient,handleIngredientChange,id} = props;

  function handleChange(changes){
    handleIngredientChange(id,{...ingredient,...changes})
  }
  return (


      <>
        <input 
        type="text" 
        className="recipe-edit__input" 
        value={ingredient.name} 
        onChange={(event)=>handleChange({name:event.target.value})}/>
        <input type="text" className="recipe-edit__input" value={ingredient.amount}/>
        <button className="btn btn--danger">&times;</button>
      </>



  );
}
