import React, { useContext, useState } from "react";
import { RecipeContext } from "./App";
import Recipe from "./Recipe";
import Search from "./Search";

export default function RecipeList({ recipes }) {
  const { handleRecipeAdd } = useContext(RecipeContext);
  const { search } = window.location;
  const query = new URLSearchParams(search).get("search");
  const [searchQuery, setSearchQuery] = useState(query || "");

  return (
    <>
      <div className="recipe-list">
        <Search setSearchQuery={setSearchQuery} searchQuery={searchQuery} />
        <div>
          {recipes.map((recipe) => {
            if (searchQuery === recipe.name) {
              return (
                <>
                  <div className="recipe__header">
                    <h3 className="recipe__title">{recipe.name}</h3>
                    <div>
                      <button
                        className="btn btn--primary mr-1"
                        onClick={() => handleRecipeSelect(id)}
                      >
                        Edit
                      </button>
                      <button
                        className="btn btn--danger"
                        onClick={() => handleRecipeDelete(id)}
                      >
                        Delete
                      </button>
                    </div>
                  </div>
                  <div className="recipe__row">
                    <span className="recipe__label">Cook Time: </span>
                    <span className="recipe__value">{recipe.cookTime}</span>
                  </div>
                  <div className="recipe__row">
                    <span className="recipe__label">Servings: </span>
                    <span className="recipe__value">{recipe.servings}</span>
                  </div>
                  <div className="recipe__row">
                    <span className="recipe__label">Instructions: </span>
                    <div className="recipe__value recipe__value--instructions recipe__value--indented">
                      {recipe.instructions}
                    </div>
                  </div>
                  <div className="recipe__row">
                    <span className="recipe__label">Ingredients: </span>
                    <div className="recipe__value recipe__value--indented">
                      <IngredienstList ingredients={recipe.ingredients} />
                    </div>
                  </div>
                </>
              );
            }
            return <Recipe key={recipe.id} {...recipe} />;
          })}
        </div>

        <div className="recipe-list__add-recipe-btn-container">
          <button className="btn btn--primary" onClick={handleRecipeAdd}>
            Add a recipe
          </button>
        </div>
      </div>
    </>
  );
}
