import React, { useContext,useState } from "react";
import { RecipeContext } from "./App";
import Recipe from "./Recipe";
import Search from "./Search";

export default function RecipeList({ recipes }) {
  const { handleRecipeAdd } = useContext(RecipeContext);
  const {search} = window.location;
  const query= new URLSearchParams(search).get('search');
  const [searchQuery,setSearchQuery] = useState(query||"");
  const filteredRecipe = filterRecipe(recipes,searchQuery);

  function filterRecipe(recipes,searchQuery){
    recipes.map(recipe=>{
      if(!searchQuery){
        return <Recipe key={recipe.id} {...recipe}/>
      }
      return recipes.filter(recipe=>recipe.name.includes(searchQuery));
    })

  }

  return (
    <>
      <div className="recipe-list">
        <Search
           setSearchQuery = {setSearchQuery}
           searchQuery = {searchQuery}
           />
        <div>
          {filteredRecipe.map((recipe) => {
            {/* if(searchQuery === recipe.name){
              return <Recipe key={recipe.id} {filteredRecipe}/>
            }else{
              return <Recipe key={recipe.id} {...recipe}/>;
            } */}
               return <li key={recipe.id} {...recipe}/>

          })}
        </div>

        <div className="recipe-list__add-recipe-btn-container">
          <button className="btn btn--primary" onClick={handleRecipeAdd}>
            Add a recipe
          </button>
        </div>
      </div>
    </>
  );
}
